import React, { useState, useEffect, useCallback, useRef } from 'react';
import Bird from './Bird';
import Pipe from './Pipe';
import gameOverSound from '../assets/bruh.mp3';
import backgroundMusic from '../assets/BasshunterDotA8bit.mp3';

const GRAVITY = 0.7;
const JUMP_FORCE = -8;
const PIPE_GAP = 150;
const PIPE_INTERVAL = 2000;
const BASE_PIPE_SPEED = 6.25;
const SPEED_INCREASE = 0.025; // 2.5% increase per pipe

const Game: React.FC = () => {
  const [birdPosition, setBirdPosition] = useState(250);
  const [velocity, setVelocity] = useState(0);
  const [pipes, setPipes] = useState<{ x: number; height: number; passed: boolean }[]>([]);
  const [isGameOver, setIsGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [speedMultiplier, setSpeedMultiplier] = useState(1);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const musicRef = useRef<HTMLAudioElement | null>(null);

  useEffect(() => {
    // Create audio elements when component mounts
    audioRef.current = new Audio(gameOverSound);
    musicRef.current = new Audio(backgroundMusic);
    if (musicRef.current) {
      musicRef.current.loop = true; // Enable looping
      musicRef.current.volume = 0.5; // Set volume to 50%
    }
  }, []);

  const playGameOverSound = () => {
    if (audioRef.current) {
      audioRef.current.currentTime = 0;
      audioRef.current.play();
    }
    // Pause background music on game over
    if (musicRef.current) {
      musicRef.current.pause();
    }
  };

  const startBackgroundMusic = () => {
    if (musicRef.current) {
      musicRef.current.currentTime = 0;
      musicRef.current.play();
    }
  };

  const jump = useCallback(() => {
    if (!isGameOver) {
      setVelocity(JUMP_FORCE);
      // Start music on first jump
      if (musicRef.current && musicRef.current.paused) {
        startBackgroundMusic();
      }
    }
  }, [isGameOver]);

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.code === 'Space') {
        jump();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [jump]);

  useEffect(() => {
    if (isGameOver) return;

    const gameLoop = setInterval(() => {
      setBirdPosition((prev) => {
        const newPosition = prev + velocity;
        if (newPosition > 500 || newPosition < 0) {
          setIsGameOver(true);
          return prev;
        }
        return newPosition;
      });
      setVelocity((prev) => prev + GRAVITY);
    }, 20);

    return () => clearInterval(gameLoop);
  }, [velocity, isGameOver]);

  useEffect(() => {
    if (isGameOver) return;

    const pipeGenerator = setInterval(() => {
      const height = Math.random() * (400 - PIPE_GAP);
      setPipes((prev) => [...prev, { x: 800, height, passed: false }]);
    }, PIPE_INTERVAL);

    return () => clearInterval(pipeGenerator);
  }, [isGameOver]);

  useEffect(() => {
    if (isGameOver) return;

    const pipeInterval = setInterval(() => {
      setPipes((prev) => {
        return prev
          .map((pipe) => {
            // Move pipe to the left with current speed multiplier
            const newX = pipe.x - (BASE_PIPE_SPEED * speedMultiplier);

            // Check collision with more precise hitbox
            const birdLeft = 50;  // Bird's x position
            const birdRight = birdLeft + 40;  // Bird width is 40px
            const birdTop = birdPosition;
            const birdBottom = birdPosition + 40;  // Bird height is 40px

            const pipeLeft = newX;
            const pipeRight = newX + 60;  // Pipe width is 60px

            if (
              birdRight > pipeLeft && 
              birdLeft < pipeRight && 
              (birdTop < pipe.height || birdBottom > pipe.height + PIPE_GAP)
            ) {
              setIsGameOver(true);
              playGameOverSound();
            }

            // Check if pipe passed the bird
            if (!pipe.passed && newX < 50) {
              setScore((s) => s + 1);
              setSpeedMultiplier(prev => prev + SPEED_INCREASE); // Increase speed when passing a pipe
              return { ...pipe, x: newX, passed: true };
            }

            return { ...pipe, x: newX };
          })
          .filter((pipe) => pipe.x > -100); // Remove pipes that are off screen
      });
    }, 16); // 60 FPS

    return () => clearInterval(pipeInterval);
  }, [isGameOver, birdPosition, speedMultiplier]);

  const restartGame = () => {
    setBirdPosition(250);
    setVelocity(0);
    setPipes([]);
    setScore(0);
    setSpeedMultiplier(1);
    setIsGameOver(false);
    // Restart background music
    startBackgroundMusic();
  };

  return (
    <div
      onClick={jump}
      style={{
        height: '500px',
        width: '100%',
        backgroundColor: 'lightblue',
        position: 'relative',
        overflow: 'hidden',
        cursor: 'pointer',
      }}
    >
      <Bird position={birdPosition} velocity={velocity} />
      {pipes.map((pipe, i) => (
        <div key={i} style={{ position: 'absolute', left: pipe.x, top: 0, height: '100%' }}>
          <Pipe height={pipe.height} top />
          <Pipe height={500 - pipe.height - PIPE_GAP} top={false} />
        </div>
      ))}

      <div
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          fontSize: '24px',
          fontWeight: 'bold',
        }}
      >
        Score: {score}
      </div>

      {isGameOver && (
        <div
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            textAlign: 'center',
          }}
        >
          <h1>Game Over!</h1>
          <button
            onClick={(e) => {
              e.stopPropagation();
              restartGame();
            }}
            style={{
              padding: '10px 20px',
              fontSize: '18px',
              cursor: 'pointer',
            }}
          >
            Restart
          </button>
        </div>
      )}
    </div>
  );
};

export default Game;
